<template>
  <div>
    <div id="newEvent">
      <div class="title">
        <img src="data:image/png;base64," alt="">
      </div>
      <div class="container clearfix">
        <div v-for="data in newEventList" :key="data.categoryId" @click="handleClick(data.categoryId)">
          <img :src="data.imageUrl" alt="">
          <div class="event-base">
            <span>{{data.englishName}}</span>
            <span>{{data.chineseName}}</span>
            <span>{{data.discountText}}</span>
          </div>
        </div>
      </div>
    </div>
    <div id="hotBrand">
      <div class="title">
        <img src="data:image/png;base64," alt="">
      </div>
      <div class="container clearfix">
        <div v-for="data in hotBrandList" :key="data.categoryId" @click="handleClick(data.categoryId)">
          <img :src="data.imageUrl" alt="">
          <div class="event-base">
            <span id="span1">{{data.englishName}}</span>
            <span>{{data.chineseName}}</span>
            <span>{{data.discountText}}</span>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import http from '@/util/http.js'
import { mapMutations } from 'vuex'
export default {
  data () {
    return {
      // 今日新品数据
      newEventList: '',
      // 热卖品牌数据
      hotBrandList: ''
    }
  },
  methods: {
    // 解构 vuex里的控制tabbar的显示的方法
    ...mapMutations('TabbarModule', ['Hide']),
    // 点击跳转到产品列表页
    handleClick (categoryId) {
      this.$router.push(`/productlist/${categoryId}`)
    }
  },
  mounted () {
    // 进入页面，将顶部选项栏显示出来
    this.Hide()
    // 获取首页数据
    http({
      url: '/home/eventForH5?params=%7B%7D&timestamp=1614781687943&summary=9f970d3059aa8f7e289b123a7cf4f839&platform_code=H5'
    }).then(res => {
      // 获取今日新品数据
      this.newEventList = res.data.lists[0].events
      // 获取热卖品牌数据
      this.hotBrandList = res.data.lists[1].events
    })
  }
}
</script>

